function [t,Y] = RK4method(F,t0,y0,T,dt)
   t = t0:dt:t0+T;
   N = length(t)-1;
   Y = zeros(N+1,1);
   Y(1) = y0;
   for ii = 2:N + 1
   tn1 = t(ii-1);
   tn2 = t(ii-1) +0.5*dt;
   tn3 = t(ii-1) +0.5*dt;
   tn4 = t(ii-1) + dt;
   Yn1 = Y(ii-1);
   Yn2 = Y(ii-1) + 0.5*dt*F(tn1,Yn1); 
   Yn3 = Y(ii-1) + 0.5*dt*F(tn2,Yn2);
   Yn4 = Y(ii-1) + dt*F(tn3,Yn3);
   Y(ii) = Y(ii-1) + (1/6)*dt*(F(tn1,Yn1) + 2*F(tn2,Yn2) + 2*F(tn3,Yn3)...
       + F(tn4,Yn4));        
   end                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
end